svg.script:
  	var moon = document.getElementById("moon");
  
	for (var i=70; i >= 0; i-=10){
		var path = document.createElementNS('http://www.w3.org/2000/svg', 'path');		
		path.setAttribute('stroke','#000');
		path.setAttribute('fill','white');
		path.setAttribute('d',[
			'M', 100, 100,
			'c'+[i, 0], [i, 100], [0, 100],
			'c'+[-i, 0], [-i, -100], [0, -100],
			'z'
		].join(' '));
		
		moon.appendChild(path);
	};
